import React from "react";
import {
  Segment,
  Label,
  Grid
} from "semantic-ui-react";

/**
 * Takes in the order array and returns the segment displaying
 * the subtotal and delivery fee. Delivery fee is currently a 
 * flat $5 fee.
 */
const SubtotalAndDeliveryFee = ({order}) => (
    <Segment inverted>
      <Grid>
        <Grid.Row>
          <Grid.Column width={15}>
            <Label color="grey">Subtotal</Label>
          </Grid.Column>
          <Grid.Column style={{textAlign: 'right'}}>
            {new Intl.NumberFormat("en-US", {
              style: "currency",
              currency: "USD"
            }).format(
              order.reduce((prev, curr) => prev + curr.price * curr.qty, 0)
            )}
          </Grid.Column>
        </Grid.Row>
        <Grid.Row>
          <Grid.Column width={15}>
            <Label color="lightgray">Delivery Fee</Label>
          </Grid.Column>
          <Grid.Column style={{textAlign: 'right'}}>
            {new Intl.NumberFormat("en-US", {
              style: "currency",
              currency: "USD"
            }).format(5)}
          </Grid.Column>
        </Grid.Row>
      </Grid>
    </Segment>
);

export default SubtotalAndDeliveryFee;
